/* ================================================ */
/* STUDIO DESIGN SYSTEM - GENERATEBLOCKS STYLES   */
/* ================================================ */

/* CSS Variables - Add these to GB Styles CSS Editor */
:root {
    /* Studio Colors */
    --studio-primary-500: rgb(39, 104, 96);    /* Studio teal */
    --studio-secondary-500: rgb(112, 153, 51); /* Studio green */

    /* Neutrals */
    --studio-base-50: #ffffff;
    --studio-base-100: #f8f8f8;
    --studio-base-200: #e8e8e8;
    --studio-base-300: #d1d1d1;
    --studio-base-400: #a0a0a0;
    --studio-base-500: #737373;
    --studio-base-600: #525252;
    --studio-base-700: #404040;
    --studio-base-800: #262626;
    --studio-base-900: #171717;
    --studio-base-950: #000000;

    /* Typography */
    --studio-font-family: 'Montserrat', system-ui, sans-serif;
    --studio-text-metadata: 0.75rem;
    --studio-text-interface: 0.875rem;
    --studio-text-body: 1rem;
    --studio-text-card-title: 1.125rem;
    --studio-text-section-title: 1.25rem;
    --studio-text-page-title: 1.875rem;

    /* Spacing */
    --studio-space-1: 0.25rem;
    --studio-space-2: 0.5rem;
    --studio-space-3: 0.75rem;
    --studio-space-4: 1rem;
    --studio-space-5: 2rem;
    --studio-space-6: 3rem;

    /* Border Radius */
    --studio-radius-sm: 0.375rem;
    --studio-radius: 0.5rem;
    --studio-radius-lg: 0.75rem;
    --studio-radius-xl: 1rem;

    /* Border Thickness */
    --studio-border-thin: 1px;
    --studio-border-medium: 2px;
    --studio-border-thick: 3px;
}

/* Light Mode Theme Colors */
:root, [data-theme="light"] {
    --studio-bg-main: var(--studio-base-50);
    --studio-bg-header: var(--studio-base-200);
    --studio-bg-sidebar: var(--studio-base-100);
    --studio-bg-card: var(--studio-base-200);
    --studio-bg-input: var(--studio-base-200);

    --studio-border-ui: var(--studio-base-300);
    --studio-border-primary: var(--studio-base-300);

    --studio-text-content: var(--studio-base-950);
    --studio-text-supporting: var(--studio-base-700);
    --studio-text-subtle: var(--studio-base-600);
    --studio-text-navigation: var(--studio-base-900);
    --studio-text-emphasis: var(--studio-base-50);
}

/* Dark Mode Theme Colors */
[data-theme="dark"] {
    --studio-bg-main: var(--studio-base-950);
    --studio-bg-header: var(--studio-base-800);
    --studio-bg-sidebar: var(--studio-base-900);
    --studio-bg-card: var(--studio-base-800);
    --studio-bg-input: var(--studio-base-800);

    --studio-border-ui: var(--studio-base-700);
    --studio-border-primary: var(--studio-base-700);

    --studio-text-content: var(--studio-base-50);
    --studio-text-supporting: var(--studio-base-300);
    --studio-text-subtle: var(--studio-base-500);
    --studio-text-navigation: var(--studio-base-100);
    --studio-text-emphasis: var(--studio-base-950);
}

/* ================================================ */
/* GENERATEBLOCKS COMPATIBLE STYLES                */
/* ================================================ */

/* Studio App Container - works with any %block% */
.gb-element.studio-app,
[class*="studio-app-"] {
    font-family: var(--studio-font-family);
    background: var(--studio-bg-main);
    color: var(--studio-text-content);
    min-height: 100vh;
}

/* Studio Layout Grid */
.gb-element.studio-layout,
[class*="studio-layout-"] {
    display: grid;
    grid-template-columns: 280px 1fr 320px;
    min-height: 100vh;
    gap: 0;
}

/* ================================================ */
/* LEFT SIDEBAR STYLES                             */
/* ================================================ */

.gb-element.studio-sidebar,
[class*="studio-sidebar-"] {
    background: var(--studio-bg-sidebar);
    border-right: var(--studio-border-thin) solid var(--studio-border-ui);
    display: flex;
    flex-direction: column;
    padding: 0;
}

/* Studio Header */
.gb-element.studio-header,
[class*="studio-header-"] {
    background: var(--studio-bg-header);
    padding: var(--studio-space-5);
    border-bottom: var(--studio-border-thin) solid var(--studio-border-ui);
}

.gb-element.studio-brand,
[class*="studio-brand-"] {
    display: flex;
    align-items: center;
    gap: var(--studio-space-3);
}

.studio-logo,
[class*="studio-logo-"] {
    width: 35px;
    height: 35px;
}

.gb-element.studio-title,
[class*="studio-title-"] {
    font-size: var(--studio-text-section-title);
    font-weight: 700;
    background: linear-gradient(135deg, var(--studio-primary-500), var(--studio-secondary-500));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    letter-spacing: 1px;
}

/* Navigation */
.gb-element.studio-nav,
[class*="studio-nav-"] {
    padding: var(--studio-space-5);
    flex: 1;
}

.gb-element.studio-nav-section,
[class*="studio-nav-section-"] {
    margin-bottom: var(--studio-space-6);
}

.gb-element.studio-nav-title,
[class*="studio-nav-title-"] {
    font-size: var(--studio-text-metadata);
    color: var(--studio-text-subtle);
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    margin-bottom: var(--studio-space-3);
}

.studio-nav-item,
[class*="studio-nav-item-"] {
    display: flex;
    align-items: center;
    gap: var(--studio-space-2);
    padding: var(--studio-space-2) var(--studio-space-3);
    color: var(--studio-text-supporting);
    text-decoration: none;
    font-size: var(--studio-text-interface);
    border-radius: var(--studio-radius-sm);
    margin-bottom: var(--studio-space-1);
    transition: all 0.2s ease;
    font-weight: 500;
}

.studio-nav-item:hover,
[class*="studio-nav-item-"]:hover {
    background: var(--studio-bg-card);
    color: var(--studio-text-content);
}

.studio-nav-item.active,
[class*="studio-nav-item-"].active {
    background: linear-gradient(135deg, var(--studio-primary-500), var(--studio-secondary-500));
    color: var(--studio-text-emphasis);
    font-weight: 500;
}

.studio-nav-icon,
[class*="studio-nav-icon-"] {
    width: 16px;
    height: 16px;
    font-size: var(--studio-text-interface);
}

/* ================================================ */
/* MAIN CONTENT STYLES                             */
/* ================================================ */

.gb-element.studio-main,
[class*="studio-main-"] {
    display: flex;
    flex-direction: column;
    overflow: hidden;
}

/* Top Header */
.gb-element.studio-top-header,
[class*="studio-top-header-"] {
    padding: var(--studio-space-4) var(--studio-space-6);
    border-bottom: var(--studio-border-thin) solid var(--studio-border-ui);
    background: var(--studio-bg-header);
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: 100px;
}

.gb-element.studio-main-title,
[class*="studio-main-title-"] {
    font-size: var(--studio-text-page-title);
    font-weight: 700;
    background: linear-gradient(135deg, var(--studio-primary-500), var(--studio-secondary-500));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin: 0;
}

.gb-element.studio-controls,
[class*="studio-controls-"] {
    display: flex;
    gap: var(--studio-space-3);
    align-items: center;
}

.studio-theme-toggle,
[class*="studio-theme-toggle-"] {
    width: 48px;
    height: 24px;
    border: var(--studio-border-medium) solid var(--studio-border-ui);
    border-radius: 14px;
    background: var(--studio-bg-sidebar);
    color: var(--studio-text-subtle);
    cursor: pointer;
    transition: all 0.2s ease;
    font-size: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
}

.studio-theme-toggle:hover,
[class*="studio-theme-toggle-"]:hover {
    border-color: var(--studio-primary-500);
    color: var(--studio-primary-500);
}

/* Section Header */
.gb-element.studio-section-header,
[class*="studio-section-header-"] {
    padding: var(--studio-space-6) var(--studio-space-6) var(--studio-space-3);
}

.gb-element.studio-section-container,
[class*="studio-section-container-"] {
    background: var(--studio-bg-card);
    border-radius: var(--studio-radius-xl);
    padding: 0;
    display: grid;
    grid-template-columns: auto 1fr;
    gap: 0;
    align-items: center;
    border: var(--studio-border-thick) solid var(--studio-border-primary);
    overflow: hidden;
}

.gb-element.studio-section-title-stack,
[class*="studio-section-title-stack-"] {
    display: flex;
    flex-direction: column;
    gap: var(--studio-space-1);
    padding: var(--studio-space-5);
    background: var(--studio-bg-header);
}

.gb-element.studio-section-title,
[class*="studio-section-title-"] {
    font-size: var(--studio-text-section-title);
    font-weight: 700;
    color: var(--studio-text-content);
    margin: 0;
}

.gb-element.studio-section-subtitle,
[class*="studio-section-subtitle-"] {
    font-size: var(--studio-text-interface);
    color: var(--studio-text-supporting);
    margin: 0;
}

.gb-element.studio-section-description,
[class*="studio-section-description-"] {
    padding: var(--studio-space-5);
}

.gb-element.studio-description-text,
[class*="studio-description-text-"] {
    color: var(--studio-text-supporting);
    font-size: var(--studio-text-interface);
    line-height: 1.5;
    margin: 0;
}

/* Content Area */
.gb-element.studio-content,
[class*="studio-content-"] {
    padding: var(--studio-space-6);
    flex: 1;
    overflow-y: auto;
}

/* ================================================ */
/* COLOR GRID STYLES                               */
/* ================================================ */

.gb-element.studio-color-grid,
[class*="studio-color-grid-"] {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: var(--studio-space-4);
}

.gb-element.studio-color-card,
[class*="studio-color-card-"] {
    background: var(--studio-bg-card);
    border: var(--studio-border-thick) solid var(--studio-border-primary);
    border-radius: var(--studio-radius-xl);
    padding: var(--studio-space-4);
    transition: all 0.2s ease;
}

.studio-color-card:hover,
[class*="studio-color-card-"]:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
}

.gb-element.studio-color-swatch,
[class*="studio-color-swatch-"] {
    width: 100%;
    height: 120px;
    border-radius: var(--studio-radius-lg);
    margin-bottom: var(--studio-space-3);
    border: var(--studio-border-thick) solid var(--studio-border-ui);
}

.gb-element.studio-color-info,
[class*="studio-color-info-"] {
    padding: var(--studio-space-1);
}

.gb-element.studio-color-name,
[class*="studio-color-name-"] {
    font-size: var(--studio-text-card-title);
    font-weight: 600;
    color: var(--studio-text-content);
    margin: 0 0 var(--studio-space-3) 0;
}

.gb-element.studio-color-values,
[class*="studio-color-values-"] {
    display: flex;
    flex-direction: column;
    gap: var(--studio-space-2);
}

.gb-element.studio-color-format,
[class*="studio-color-format-"] {
    display: flex;
    align-items: center;
    gap: var(--studio-space-2);
}

.gb-element.studio-format-label,
[class*="studio-format-label-"] {
    font-size: var(--studio-text-metadata);
    font-weight: 600;
    color: var(--studio-text-subtle);
    width: 40px;
    text-transform: uppercase;
}

.studio-color-input,
[class*="studio-color-input-"] {
    flex: 1;
    padding: var(--studio-space-2);
    border: var(--studio-border-thin) solid var(--studio-border-ui);
    border-radius: var(--studio-radius);
    background: var(--studio-bg-input);
    color: var(--studio-text-content);
    font-family: var(--studio-font-family);
    font-size: var(--studio-text-interface);
}

/* Add Color Card */
.gb-element.studio-add-color-card,
[class*="studio-add-color-card-"] {
    border: var(--studio-border-medium) dashed var(--studio-border-ui);
    background: transparent;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    text-align: center;
    cursor: pointer;
    transition: all 0.2s ease;
    opacity: 0.7;
}

.studio-add-color-card:hover,
[class*="studio-add-color-card-"]:hover {
    opacity: 1;
    border-color: var(--studio-primary-500);
    transform: translateY(-2px);
}

.gb-element.studio-add-color-swatch,
[class*="studio-add-color-swatch-"] {
    display: flex;
    align-items: center;
    justify-content: center;
    background: var(--studio-bg-card);
    border: var(--studio-border-medium) dashed var(--studio-border-ui);
}

.gb-element.studio-add-color-icon,
[class*="studio-add-color-icon-"] {
    font-size: var(--studio-text-page-title);
    color: var(--studio-text-subtle);
    font-weight: 300;
}

.gb-element.studio-add-color-title,
[class*="studio-add-color-title-"] {
    font-size: var(--studio-text-card-title);
    font-weight: 600;
    color: var(--studio-text-content);
    margin: 0 0 var(--studio-space-1) 0;
}

.gb-element.studio-add-color-subtitle,
[class*="studio-add-color-subtitle-"] {
    font-size: var(--studio-text-interface);
    color: var(--studio-text-subtle);
    margin: 0;
}

/* ================================================ */
/* RIGHT SIDEBAR STYLES                            */
/* ================================================ */

.gb-element.studio-details,
[class*="studio-details-"] {
    background: var(--studio-bg-sidebar);
    border-left: var(--studio-border-thin) solid var(--studio-border-ui);
    padding: var(--studio-space-5);
    overflow-y: auto;
}

/* Client Selector */
.gb-element.studio-client-selector,
[class*="studio-client-selector-"] {
    margin-bottom: var(--studio-space-6);
    padding-bottom: var(--studio-space-5);
    border-bottom: var(--studio-border-thin) solid var(--studio-border-ui);
}

/* Color Editor */
.gb-element.studio-color-editor,
[class*="studio-color-editor-"] {
    /* Additional styling if needed */
}

.gb-element.studio-editor-title,
[class*="studio-editor-title-"] {
    font-size: var(--studio-text-section-title);
    font-weight: 600;
    color: var(--studio-text-content);
    margin: 0 0 var(--studio-space-4) 0;
}

/* Form Elements */
.gb-element.studio-field,
[class*="studio-field-"] {
    margin-bottom: var(--studio-space-3);
}

.gb-element.studio-field-label,
[class*="studio-field-label-"] {
    display: block;
    font-size: var(--studio-text-interface);
    font-weight: 500;
    color: var(--studio-text-supporting);
    margin-bottom: var(--studio-space-2);
}

.studio-input,
.studio-select,
[class*="studio-input-"],
[class*="studio-select-"] {
    width: 100%;
    padding: var(--studio-space-3);
    border: var(--studio-border-thin) solid var(--studio-border-ui);
    border-radius: var(--studio-radius);
    background: var(--studio-bg-input);
    color: var(--studio-text-content);
    font-family: var(--studio-font-family);
    font-size: var(--studio-text-interface);
}

.studio-input:focus,
.studio-select:focus,
[class*="studio-input-"]:focus,
[class*="studio-select-"]:focus {
    outline: none;
    border-color: var(--studio-primary-500);
    box-shadow: 0 0 0 2px rgba(39, 104, 96, 0.2);
}

.studio-button,
[class*="studio-button-"] {
    padding: var(--studio-space-3) var(--studio-space-4);
    border: var(--studio-border-thin) solid var(--studio-primary-500);
    border-radius: var(--studio-radius);
    background: var(--studio-primary-500);
    color: white;
    font-family: var(--studio-font-family);
    font-size: var(--studio-text-interface);
    font-weight: 600;
    cursor: pointer;
    transition: all 0.2s ease;
    width: 100%;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.studio-button:hover,
[class*="studio-button-"]:hover {
    background: var(--studio-primary-600);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(39, 104, 96, 0.3);
}

/* ================================================ */
/* RESPONSIVE DESIGN                                */
/* ================================================ */

@media (max-width: 1200px) {
    .gb-element.studio-layout,
    [class*="studio-layout-"] {
        grid-template-columns: 240px 1fr 280px;
    }
}

@media (max-width: 768px) {
    .gb-element.studio-layout,
    [class*="studio-layout-"] {
        grid-template-columns: 1fr;
        grid-template-rows: auto 1fr auto;
    }

    .gb-element.studio-details,
    [class*="studio-details-"] {
        order: 3;
    }
}
